import { useState,useRef } from "react";
import "./home.css";
import SearchResultsList from "../../components/SearchResultsList/SearchResultsList";
import SearchBar from "../../components/Searchbar/SearchBar";
import { useNavigate } from "react-router-dom";

function Home() {
    const [results, setResults] = useState([]);
    const [selectedItem, setSelectedItem] = useState(-1);
    const [search, setSearch] = useState();
    const navigate = useNavigate();
    const searchContainer = useRef(null);
    const searchResultRef = useRef(null);
    const handleKeyDown = e => {
        if (selectedItem < results.length) {
            if (e.key === "ArrowUp" && selectedItem > 0) {
                setSelectedItem(prev => prev - 1);
                setSearch(`${results[selectedItem].first_name} ${results[selectedItem].last_name}`)
            }
            else if (e.key === "ArrowDown" && selectedItem < results.length - 1) {
                setSelectedItem(prev => prev + 1);
                setSearch(`${results[selectedItem].first_name} ${results[selectedItem].first_name}`)
            }
            else if (e.key === "Enter") {

                navigate(`/user/${results[selectedItem].id.$oid}`)
            }

        }else{
            setSelectedItem(-1)
        }
    };
    return (
        <div className="Home">
            <div className="search-bar-container" tabIndex={-1}
                onKeyDown={handleKeyDown}>
                <SearchBar setResults={setResults} results={results} search={search} />
                {results && <SearchResultsList results={results} selectedItem={selectedItem
                } />}
            </div>
        </div>
    );
}

export default Home;
